namespace Magestore\OneStepCheckout\Controller\Index;

use Magento\Framework\App\ResponseInterface;

/**
 * class ValidateEmail
 *
 * @category Magestore
 * @package  Magestore_OneStepCheckout
 * @module   OneStepCheckout
 * @author   Magestore Developer
 */
class ValidateEmail extends \Magestore\OneStepCheckout\Controller\Index
{
    /**
     * Dispatch request
     *
     * @return \Magento\Framework\Controller\ResultInterface|ResponseInterface
     * @throws \Magento\Framework\Exception\NotFoundException
     */
    public function execute()
    {
        /** @var \Magento\Framework\Controller\Result\Json $resultJson */
        $resultJson = $this->_resultJsonFactory->create();

        /** @var \Magento\Framework\DataObject $onestepData */
        $email = $this->_getParamDataObject()->getData('email');

        return $resultJson->setData(['message_code' => $this->_validator->validateEmail($email)]);
    }
}